\mainmatter
\chapter{تشریح مسئله و جدیت نیاز}\label{chap:problem}

\section{خلأ نهادی در جنبش گذارخواهی ایران}

از جنبش سبز ۱۳۸۸ تا خیزش ژینا ۱۴۰۱ و ادامه‌ی آن، جنبش مردمی ایران
با رشد کمّی و کیفی چشمگیری همراه بوده، اما یک عنصر ساختاری همچنان
غایب است: \keyword{سازوکار تصمیم‌گیری حقوقی‌ـ‌اخلاقی}
برای مس

# بخش چهارم: ادامه‌ی فصل دوم، فصل سوم و فصل چهارم

ادامه‌ی دقیق کد LaTeX:

---

```latex
% ============================================================
%  ادامه از جایی که قطع شد — دستاوردهای کوتاه‌مدت
% ============================================================

جع واحد مستندسازی:}
        ایجاد یک بانک اطلاعاتی یکپارچه از جرایم حکومتی با
        استانداردهای حقوق بین‌الملل، قابل ارائه به دادگاه‌های ملی
        و بین‌المللی.

  \item \textbf{اعتبار رسانه‌ای:}
        وقتی یک نهاد ساختارمند با اعضای شناخته‌شده‌ی آکادمیک
        «رأی» صادر می‌کند، بازتاب رسانه‌ای آن چندین برابر
        یک بیانیه‌ی سیاسی معمول است.

  \item \textbf{کاهش فرسایش درون‌جنبشی:}
        بسیاری از مناقشات داخلی اپوزیسیون (مثلاً درباره‌ی تحریم‌ها)
        ناشی از نبودِ یک مرجع داوری معتبر است.
        داشتن یک نهاد مشورتی مستقل می‌تواند سطح تنش را کاهش دهد.

  \item \textbf{سپر دفاعی در برابر جنگ روانی:}
        هر توصیه‌ی نهاد، با مستندات و آراء مخالف منتشر می‌شود.
        این شفافیت، اصلی‌ترین سلاح در برابر تبلیغات حکومتی است.
\end{enumerate}

\subsection{دستاوردهای میان‌مدت (۱ تا ۳ سال)}

\begin{enumerate}[nosep, label=\textcolor{cGold}{\alph*.}]
  \item \textbf{انباشت حقوقی:}
        مجموعه‌ای از «آراء مشورتی» شکل می‌گیرد که
        \keyword{رویه‌ی قضایی سایه} (\en{Shadow Case Law})
        را بنیان می‌گذارد — قابل استفاده در دوره‌ی گذار.

  \item \textbf{تقویت پرونده‌های بین‌المللی:}
        مستندات نهاد می‌تواند به‌عنوان
        \en{amicus curiae} یا مدرک تکمیلی
        در پرونده‌های \en{Universal Jurisdiction}
        مورد استفاده قرار گیرد.

  \item \textbf{جذب اعتماد بین‌المللی:}
        دولت‌ها و نهادهای بین‌المللی تمایل بیشتری به تعامل با
        اپوزیسیونی ساختارمند دارند. وجود یک نهاد شبه‌قضایی
        معتبر، \keyword{سرمایه‌ی اعتماد} (\en{trust capital})
        تولید می‌کند.

  \item \textbf{تربیت نسل جدید حقوقی:}
        مشارکت حقوق‌دانان جوان ایرانی در فرایندهای نهاد،
        بستر آموزشی ارزشمندی برای دوره‌ی پس از گذار فراهم می‌کند.

  \item \textbf{مدیریت منطقه‌ی خاکستری:}
        ایجاد چارچوبی برای برخورد با افراد و نهادهای «خاکستری»
        — کسانی که نه کاملاً حکومتی‌اند و نه کاملاً اپوزیسیون —
        از طریق معیارهای شفاف و قابل دفاع.
\end{enumerate}

\subsection{دستاوردهای بلندمدت (پس از گذار)}

\begin{enumerate}[nosep, label=\textcolor{cGold}{\alph*.}]
  \item \textbf{تسهیل عدالت انتقالی:}
        مستندات و آراء نهاد، پایه‌ی ارزشمندی برای
        کمیسیون حقیقت‌یاب و دادگاه‌های انتقالی خواهد بود.

  \item \textbf{الگوی نهادسازی:}
        تجربه‌ی این نهاد می‌تواند الگویی برای ساختن
        نظام قضایی مستقل در ایران آینده باشد.

  \item \textbf{حافظه‌ی تاریخی:}
        آرشیو کامل آراء، مباحثات، و شواهد،
        یک \keyword{حافظه‌ی نهادی} (\en{institutional memory})
        بی‌بدیل برای نسل‌های آینده خواهد بود.

  \item \textbf{پیشگیری از عدالت انتقامی:}
        یکی از بزرگ‌ترین خطرات پس از سقوط هر نظام استبدادی،
        «عدالت خیابانی» و انتقام‌جویی بدون فرایند است.
        وجود سوابق دادرسی مستند، احتمال این خطر را کاهش می‌دهد.
\end{enumerate}

\section{شرایط کمک‌کننده برای موفقیت}

\begin{principleBox}[پیش‌شرط‌های ضروری]
\begin{enumerate}[nosep, label=\textcolor{cPrimary}{\Roman*.}]
  \item \textbf{استقلال واقعی:}
        نهاد نباید وابسته به هیچ حزب، جناح سیاسی، یا دولت خارجی باشد.
        بودجه باید از منابع متنوع و شفاف تأمین شود.

  \item \textbf{تخصص بالا:}
        اعضای هیئت‌داوران باید دارای سوابق آکادمیک و حرفه‌ای
        قابل تأیید در حوزه‌ی حقوق بین‌الملل، حقوق بشر، یا
        حقوق کیفری باشند.

  \item \textbf{شفافیت بی‌قیدوشرط:}
        تمام آراء، مذاکرات، منابع مالی، و تعارض منافع
        باید به‌صورت عمومی قابل دسترسی باشند.

  \item \textbf{تکثرگرایی:}
        ترکیب نهاد باید بازتاب‌دهنده‌ی تنوع قومی، جنسیتی،
        نسلی، و فکری ایران باشد.

  \item \textbf{فروتنی نهادی:}
        نهاد باید همواره تأکید کند که مرجع مشورتی است و
        جایگزین نظام قضایی رسمی آینده نیست.

  \item \textbf{همکاری بین‌المللی:}
        ارتباط فعال با نهادهای بین‌المللی مانند
        \en{ICC}، \en{ICJ}، \en{OHCHR}، و
        سازمان‌های حقوق بشری.
\end{enumerate}
\end{principleBox}

