\section{نمونه‌ی ۲: دادگاه ایران (\en{Iran Tribunal}, ۲۰۱۲)}

\subsection{معرفی}

\begin{noteBox}[مشخصات]
\begin{itemize}[nosep]
  \item \textbf{سال:} ۲۰۱۲ (لاهه)
  \item \textbf{موضوع:} کشتار زندانیان سیاسی ۱۳۶۷
  \item \textbf{ماهیت:} دادگاه مردمی بین‌المللی
  \item \textbf{هیئت داوران:} شامل قاضی \en{Geoffrey Robertson QC}
        و قاضی \en{John Dugard} (حقوق‌دان برجسته‌ی آفریقای جنوبی)
  \item \textbf{نتیجه:} اعلام وقوع جنایت علیه بشریت؛
        توصیه‌ی ارجاع به \en{ICC}
\end{itemize}
\end{noteBox}

\subsection{درس‌های مثبت}

\begin{enumerate}[nosep, label=\textcolor{cGold}{\alph*.}]
  \item \textbf{حضور قضات بین‌المللی معتبر:}
        حضور \en{Robertson} و \en{Dugard} اعتبار بالایی
        به دادگاه بخشید.

  \item \textbf{مستندسازی تاریخی:}
        شهادت‌های ضبط‌شده‌ی بازماندگان، آرشیو بی‌نظیری
        از کشتار ۶۷ ایجاد کرد.

  \item \textbf{تأثیر بر پرونده‌ی نوری:}
        بسیاری از مستنداتی که در دادگاه ایران ۲۰۱۲ ارائه شدند،
        بعدها در پرونده‌ی حمید نوری در سوئد مورد استفاده قرار گرفتند.
        \keyword{این مهم‌ترین درس است:}
        مستندسازی امروز، پرونده‌ی قضایی فردا را ممکن می‌سازد.

  \item \textbf{بازتاب رسانه‌ای:}
        این دادگاه برای نخستین بار موضوع کشتار ۶۷ را
        به‌صورت ساختارمند وارد رسانه‌های بین‌المللی کرد.
\end{enumerate}

\subsection{درس‌های منفی و محدودیت‌ها}

\begin{enumerate}[nosep, label=\textcolor{cAccent}{\alph*.}]
  \item \textbf{رویداد‌محور بود، نه نهادمحور:}
        دادگاه ایران ۲۰۱۲ یک رویداد یکباره بود و
        ساختار دائمی نداشت. پس از پایان جلسات، فعالیت
        عملاً متوقف شد.
        \textbf{درس برای ما:} دادگاه در سایه باید ساختار
        دائمی و فعالیت مستمر داشته باشد.

  \item \textbf{محدود به یک موضوع:}
        فقط کشتار ۶۷ بررسی شد و سایر جرایم پوشش نیافتند.
        \textbf{درس برای ما:} صلاحیت موضوعی نهاد باید
        فراگیر و شامل تمام جرایم حکومتی باشد.

  \item \textbf{فقدان مکانیزم پیگیری:}
        پس از صدور رأی، هیچ مکانیزم رسمی‌ای برای
        پیگیری اجرای توصیه‌ها وجود نداشت.
        \textbf{درس برای ما:} دبیرخانه‌ی دائمی باید
        وظیفه‌ی رصد و پیگیری آراء را بر عهده داشته باشد.

  \item \textbf{مشارکت محدود دیاسپورا:}
        بسیاری از ایرانیان خارج از کشور از وجود دادگاه بی‌اطلاع بودند.
        \textbf{درس برای ما:} استراتژی ارتباطی قوی از ابتدا ضروری است.
\end{enumerate}


\section{نمونه‌ی ۳: دادگاه اویغورها (\en{Uyghur Tribunal}, ۲۰۲۱)}

\subsection{معرفی}

\begin{noteBox}[مشخصات]
\begin{itemize}[nosep]
  \item \textbf{سال:} ۲۰۲۱ (لندن)
  \item \textbf{موضوع:} نسل‌کشی و جرایم علیه بشریت
        در سین‌کیانگ چین
  \item \textbf{ریاست:} سر \en{Geoffrey Nice QC}
        (دادستان سابق پرونده‌ی میلوشویچ در \en{ICTY})
  \item \textbf{ماهیت:} دادگاه مردمی مستقل
  \item \textbf{نتیجه:} اعلام وقوع نسل‌کشی
\end{itemize}
\end{noteBox}

\subsection{درس‌های مثبت}

\begin{enumerate}[nosep, label=\textcolor{cGold}{\alph*.}]
  \item \textbf{ریاست با اعتبار جهانی:}
        \en{Geoffrey Nice} به‌واسطه‌ی نقشش در
        محاکمه‌ی میلوشویچ، اعتبار بی‌نظیری به دادگاه بخشید.

  \item \textbf{فرایند دقیق:}
        دادگاه اویغورها جلسات استماع عمومی، بررسی شواهد
        کارشناسی، و حق دفاع (دعوت از دولت چین برای
        ارائه‌ی دفاعیات — که البته رد شد) را رعایت کرد.

  \item \textbf{رأی مستدل و مفصل:}
        رأی نهایی بیش از ۶۰ صفحه بود و هر ادعا با
        شواهد مشخص پشتیبانی شده بود.

  \item \textbf{تأثیر سیاسی فوری:}
        رأی دادگاه به‌سرعت مورد استناد پارلمان‌های
        بریتانیا، کانادا، و هلند قرار گرفت و
        در تصمیم‌های تحریمی اتحادیه‌ی اروپا و آمریکا
        نقش ایفا کرد.

  \item \textbf{مستقل از دولت‌ها:}
        تأمین مالی از طریق \en{crowdfunding}
        و بدون بودجه‌ی دولتی — الگوی موفق استقلال مالی.
\end{enumerate}

\subsection{درس‌های منفی و محدودیت‌ها}

\begin{enumerate}[nosep, label=\textcolor{cAccent}{\alph*.}]
  \item \textbf{واکنش تهاجمی چین:}
        چین کمپین رسانه‌ای گسترده‌ای علیه دادگاه ترتیب داد.
        \textbf{درس برای ما:} باید از ابتدا برای واکنش
        تهاجمی جمهوری اسلامی آماده بود.

  \item \textbf{رویدادمحور:}
        مشابه دادگاه ایران ۲۰۱۲، ساختار دائمی نداشت.
        \textbf{درس برای ما:} تداوم نهادی.

  \item \textbf{فقدان مکانیزم اجرا:}
        رأی دادگاه اویغورها الزام‌آور نبود و
        تأثیر واقعی‌اش بسته به اراده‌ی سیاسی دولت‌ها بود.
        \textbf{درس برای ما:} تأکید بر ماهیت مشورتی
        و تمرکز بر کیفیت استدلال.
\end{enumerate}


